import React, { useState } from 'react';
import './StreamList.css'; // Relative path if StreamList.js and StreamList.css are in the same folder
//import SelectedItem from './components/SelectedItem';

const StreamList = () => {
  const [input, setInput] = useState(''); // To store the current input
  const [inputList, setInputList] = useState([]); // To store the list of submitted inputs
  //const [selectedItem, setSelectedItem] = useState(''); // Declare selectedItem with useState
  const [message, setMessage] = useState(''); // To store the message to be displayed

  const handleInputChange = (event) => {
    setInput(event.target.value); // Update input state
  };

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent page refresh
    setInputList([...inputList, input]); // Add the current input to the inputList array
    setInput(''); // Clear the input field after submission
  };
  /*const handleItemClick = (item) => {
    setSelectedItem(item); // Update selected item when a list item is clicked
  };*/
  const handleClick = () => {
    setIsDialogOpen(true); // Open dialog when button is clicked
  };

  const closeDialog = () => {
    setIsDialogOpen(false); // Close dialog
  };
  return (
    <div>

    <div className="header-container">
      <span className="material-symbols-outlined">theaters</span>
      <h1 className="inline-heading">Stream List</h1>
      <span className="material-symbols-outlined">theaters</span>
      </div>

      <form className="input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Enter something..." 
        className="input-box" // Add class for styling
        />
        <button type="submit" className="submit-button">Submit</button>
        </form>



      {/* Display the list of submitted inputs */}
      <ul className="input-list">
        {inputList.map((item, index) => (
          <li key={index}>
            {item} 
            <button className="play-button" onClick={() => handleClick(item)}>Play</button>
          </li>
        ))}
      </ul>

      {/* Dialog box */}
      {isDialogOpen && (
        <div className="dialog-overlay">
          <div className="dialog-box">
            <h1 className="dialog-message">Subscribe Now</h1>
            <button className="close-button" onClick={closeDialog}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default StreamList;
