import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid'; // Importing the uuid library
import { useNavigate } from 'react-router-dom';

import './StreamList.css'; // Relative path if StreamList.js and StreamList.css are in the same folder

const StreamList = () => {
  const [input, setInput] = useState(''); // To store the current input
  const [inputList, setInputList] = useState([]); // To store the list of submitted inputs
  const [isDialogOpen, setIsDialogOpen] = useState(false); 
  const [videoId, setVideoId] = useState(null); // To manage the video iframe ID
  const navigate = useNavigate(); // Initialize navigate

  const handleInputChange = (event) => {
    setInput(event.target.value); // Update input state
  };

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent page refresh
    if (input.trim()) { 
      const newMovie = { id: uuidv4(), name: input }; // Assign unique ID to each movie item
      setInputList([...inputList, newMovie]); // Add the current input to the inputList array
      setInput(''); // Clear the input field after submission
    }
  };

  // Remove item from the list at the given index
  const handleRemove = (index) => {
    setInputList(inputList.filter((_, i) => i !== index)); // Filter out the item at the given index
  };

  const handlePlayVideo = (movieId) => {
    setVideoId(movieId); // Set the video ID when play button is clicked
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false); // Close the dialog when "X" is clicked
  };

  const handleClick = () => {
    setIsDialogOpen(true); // Open dialog when button is clicked
  };

  const handleGoToCart = () => {
    navigate('/cart'); // Navigate to the Cart page
  };

  const handleGoToMovie = () => {
    navigate('/Movies'); // Navigate to the Movie page
  };

  return (
    <div>

      <div className="header-container">
        <span className="material-symbols-outlined">theaters</span>
        <h1 className="inline-heading">Stream List</h1>
        <span className="material-symbols-outlined">theaters</span>
      </div>

      <form className="input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Enter Movie"
          className="input-box" 
        />
        <button type="submit" className="submit-button">Add Movie</button>
        <button type="button" className="save-button" onClick={handleGoToMovie}>Save List</button> 
      </form>

      <ul className="input-list">
        {inputList.map((item, index) => (
          <li key={item.id} className="list-item">
            <span>{item.name}</span>
            <div className="button-container">
              <button className="play-button" onClick={() => handlePlayVideo(item.id)}>Play</button> 
              <button className="remove-button" onClick={() => handleRemove(index)}>Remove</button> 
            </div>
          </li>
        ))}
      </ul>

      {isDialogOpen && (
        <div className="dialog">
          <button className="close-button" onClick={handleCloseDialog}>X</button>
          <button className="go-button" onClick={handleGoToCart}>Subscribe</button> 
          <> or </>
          <button className="go-button" onClick={handleGoToCart}>Login</button>
        </div>
      )}

      {/* Embed iframe for playing video when Play button is clicked */}
      {videoId && (
        <div className="video-container">
          <iframe
            width="560"
            height="315"
            src={`https://www.youtube.com/embed/${videoId}`}
            title="Movie Player"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
      )}

    </div>
  );
};

export default StreamList;
