import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import ReactPlayer from 'react-player'; // Import the ReactPlayer library
import './StreamList.css'; // Relative path if StreamList.js and StreamList.css are in the same folder

const StreamList = () => {
  const [input, setInput] = useState(''); // To store the current input
  const [inputList, setInputList] = useState([]); // To store the list of submitted inputs
  const navigate = useNavigate(); // Initialize navigate
  const [isDialogOpen, setIsDialogOpen] = useState(false); // <-- This is important to manage dialog visibility
  const [editIndex, setEditIndex] = useState(null); // To store the index of the item being edited
  const [editText, setEditText] = useState(''); // To store the current edit text
  const [videoUrl, setVideoUrl] = useState(''); // To store the video URL to play
  const [isVideoPlaying, setIsVideoPlaying] = useState(false); // Manage video playing state

    // Play a video clip when the "Play" button is clicked
    const handlePlayVideo = (url) => {
      console.log("Playing video:", url);  // Check if the URL is being set correctly
      setVideoUrl(url);
      setIsVideoPlaying(true);
    };


  // Edit item at the given index
   const handleEdit = (index) => {
    setEditIndex(index); // Set the current item to edit mode
    setEditText(inputList[index]); // Prepopulate the edit text with the current item value
  };

  const handleEditChange = (event) => {
    setEditText(event.target.value); // Update the edit text state
  };

  const handleEditSubmit = (index) => {
    const updatedList = [...inputList];
    updatedList[index] = editText; // Update the item with the edited text
    setInputList(updatedList); // Save the updated list
    setEditIndex(null); // Exit edit mode
    setEditText(''); // Clear the edit text
  };
  const handleInputChange = (event) => {
    setInput(event.target.value); // Update input state
  };
  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent page refresh
    if (input.trim()) { // Only add non-empty inputs
      setInputList([...inputList, input]); // Add the current input to the inputList array
      setInput(''); // Clear the input field after submission
    }
  };

  // Remove item from the list at the given index
  const handleRemove = (index) => {
    setInputList(inputList.filter((_, i) => i !== index)); // Filter out the item at the given index
  };
  const handleCloseDialog = () => {
    setIsDialogOpen(false); // Close the dialog when "X" is clicked
  };
  // eslint-disable-next-line 
  const handleClick = () => {
    setIsDialogOpen(true); // Open dialog when button is clicked
  };
  const handleGoToCart = () => {
    navigate('/cart'); // Navigate to the Cart page
  };
  const handleGoToMovie = () => {
    navigate('/Movies'); // Navigate to the Movie page
  };

  return (
    <div>

    <div className="header-container">
      <span className="material-symbols-outlined">theaters</span>
      <h1 className="inline-heading">Stream List</h1>
      <span className="material-symbols-outlined">theaters</span>
      </div>

      <form className="input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Enter Movie" 
        className="input-box" // Add class for styling
        />
        <button type="submit" className="submit-button">Add Movie</button>
        <button type="button" className="save-button" onClick={handleGoToMovie}>Save List</button> {/* Save button */}
        </form>

        <ul className="input-list">
        {inputList.map((item, index) => (
          <li key={index} className="list-item">
            {editIndex === index ? (
              <div>
                <input
                  type="text"
                  value={editText}
                  onChange={handleEditChange}
                  className="edit-input"
                />
                <button
                  className="save-button"
                  onClick={() => handleEditSubmit(index)}
                >
                  Save
                </button>
              </div>
            ) : (
              <div>
                <span>{item}</span> {/* User input */}
                <div className="button-container">
                  {/* Add Play button that will play a specific video */}
              <button 
                className="play-button" 
                onClick={() => handlePlayVideo('https://go.screenpal.com/watch/cZ6qqHVWsCG')} // Replace with actual video URL
              >
                Play
              </button>
                  <button className="edit-button" onClick={() => handleEdit(index)}>Edit</button> {/* Edit button */}
                  <button className="remove-button" onClick={() => handleRemove(index)}>Remove</button> {/* Remove button */}
                </div>
              </div>
            )}
          </li>
        ))}
      </ul>

      {/* Dialog for subscription */}
      {isDialogOpen && (
        <div className="dialog">
          <button className="close-button" onClick={handleCloseDialog}>X</button> {/* X button to close dialog */}
          {/*<h2 className="dialog-title"> Customer Plans</h2>*/}
          <button className="go-button" onClick={handleGoToCart}>Subscribe</button> 
          <> or </>
          <button className="go-button" onClick={handleGoToCart}>Login</button> 

        </div>
      )}
         {/* Video player */}
      {isVideoPlaying && (
        <div className="video-container">
          <ReactPlayer url={videoUrl} controls={true} />
        </div>
      )}
    </div>
  );
};

export default StreamList;
