import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StreamList.css';

const StreamList = () => {
  const [input, setInput] = useState(''); 
  const [inputList, setInputList] = useState([]); 
  const [searchResults, setSearchResults] = useState([]); // Initialize as an empty array
  const [selectedVideo, setSelectedVideo] = useState(null); 
  const navigate = useNavigate();
  const YOUTUBE_API_KEY = 'AIzaSyDvS2KFydtyihEeFahH7yti5cDekE8cH4g'; // Replace with your actual API key

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (input.trim()) {
      setInputList([...inputList, input]);
      setInput('');
    }
  };

  const handleSearchMovie = async (movieTitle) => {
    try {
      const response = await fetch(
        `https://www.googleapis.com/youtube/v3/search?part=snippet&type=video&q=${encodeURIComponent(movieTitle)}&key=${YOUTUBE_API_KEY}`
      );
      const data = await response.json();
      if (data.items) {
        setSearchResults(data.items); // Store the search results
      } else {
        setSearchResults([]); // Set to an empty array if no items found
      }
    } catch (error) {
      console.error("Error fetching YouTube data:", error);
      setSearchResults([]); // Handle any error by resetting the results
    }
  };

  const handleSelectVideo = (videoId) => {
    setSelectedVideo(videoId);
  };

  const handleRemove = (index) => {
    setInputList(inputList.filter((_, i) => i !== index));
  };

  return (
    <div>
      <div className="header-container">
        <span className="material-symbols-outlined">theaters</span>
        <h1 className="inline-heading">Stream List</h1>
        <span className="material-symbols-outlined">theaters</span>
      </div>

      <form className="input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Enter Movie"
          className="input-box"
        />
        <button type="submit" className="submit-button">Add Movie</button>
      </form>

      <ul className="input-list">
        {inputList.map((item, index) => (
          <li key={index} className="list-item">
            <span>{item}</span>
            <div className="button-container">
              <button className="search-button" onClick={() => handleSearchMovie(item)}>Search on YouTube</button>
              <button className="remove-button" onClick={() => handleRemove(index)}>Remove</button>
            </div>
          </li>
        ))}
      </ul>

      {/* YouTube Search Results */}
      <div className="youtube-results">
        {searchResults.length > 0 ? (
          searchResults.map((result) => (
            <div key={result.id.videoId} className="youtube-item" onClick={() => handleSelectVideo(result.id.videoId)}>
              <img src={result.snippet.thumbnails.default.url} alt={result.snippet.title} />
              <p>{result.snippet.title}</p>
            </div>
          ))
        ) : (
          <p>No results found.</p>
        )}
      </div>

      {/* Embed YouTube Video */}
      {selectedVideo && (
        <div className="video-player">
          <iframe
            width="560"
            height="315"
            src={`https://www.youtube.com/embed/${selectedVideo}`}
            title="YouTube video player"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </div>
  );
};

export default StreamList;
