import React, { useState, useEffect } from 'react';

const Movies = () => {
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  // Open the dialog when the component mounts
  useEffect(() => {
    setIsDialogOpen(true); // Open dialog when page loads
  }, []);

  const handleCloseDialog = () => {
    setIsDialogOpen(false); // Close the dialog
  };

  const handleGoToCart = () => {
    console.log('Navigating to cart page'); // Replace with navigation logic
  };

  return (
    <div>
      <h1>Movies Page</h1>

      {/* Dialog should be visible based on the state */}
      {isDialogOpen && (
        <div style={dialogStyle}> {/* Inline style for basic visibility */}
          <button style={closeButtonStyle} onClick={handleCloseDialog}>X</button>
          <h2>Access Required</h2>
          <p>You must subscribe or log in to view this page.</p>
          <button onClick={handleGoToCart}>Subscribe</button>
          <> or </>
          <button onClick={handleGoToCart}>Login</button>
        </div>
      )}
    </div>
  );
};

// Basic styling for visibility
const dialogStyle = {
  position: 'fixed',
  top: '30%',
  left: '50%',
  transform: 'translate(-50%, -30%)',
  backgroundColor: 'white',
  padding: '20px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  zIndex: 1000,
  width: '300px',
  textAlign: 'center',
};

const closeButtonStyle = {
  position: 'absolute',
  top: '5px',
  right: '10px',
  background: 'none',
  border: 'none',
  fontSize: '18px',
  cursor: 'pointer',
};

export default Movies;
