import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StreamList.css';

const StreamList = () => {
  const [input, setInput] = useState('');
  const [inputList, setInputList] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const navigate = useNavigate();
  const YOUTUBE_API_KEY = 'AIzaSyDvS2KFydtyihEeFahH7yti5cDekE8cH4g'; // Replace with your actual API key
  const [editText, setEditText] = useState(''); // To store the current text when editing
  const [editId, setEditId] = useState(null); // To track which item is being edited

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

    const handleSubmit = (event) => {
    event.preventDefault(); 
    if (input.trim()) { 
      setInputList([...inputList, { id: uuidv4(), text: input }]); // Add a new item with a unique UUID
      setInput(''); 
    }
  };

  const handleRemove = (id) => {
    setInputList(inputList.filter((item) => item.id !== id)); // Remove the item by its unique ID
  };
  const handleEdit = (id) => {
    const itemToEdit = inputList.find((item) => item.id === id);
    setEditText(itemToEdit.text); 
    setEditId(id); // Set the ID of the item being edited
  };

  const handleEditSubmit = (event) => {
    event.preventDefault();
    if (editText.trim() && editId) {
      setInputList(inputList.map(item =>
        item.id === editId ? { ...item, text: editText } : item
      ));
      setEditText(''); 
      setEditId(null); // Clear the edit ID after submission
    }
  };
  const handleSearchMovie = async (movieTitle) => {
    const response = await fetch(
      `https://www.googleapis.com/youtube/v3/search?part=snippet&type=video&q=${encodeURIComponent(movieTitle)}&key=${YOUTUBE_API_KEY}`
    );
    const data = await response.json();
    setSearchResults(data.items);
    navigate(`/movie/${movieTitle}`, { state: { searchResults: data.items } });
  };
  const handleGoToCart = () => {
    navigate('/cart'); 
  };

  return (
    <div>

      <div className="header-container">
        <span className="material-symbols-outlined">theaters</span>
        <h1 className="inline-heading">Stream List</h1>
        <span className="material-symbols-outlined">theaters</span>
      </div>

      <form className="input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Enter Movie"
          className="input-box"
        />
        <button type="submit" className="submit-button">Add Movie</button>
        <button type="button" className="save-button" onClick={handleGoToMovie}>Save List</button>
      </form>
      <ul className="input-list">
        {inputList.map((item) => (
          <li key={item.id} className="list-item">
            <span>{item.text}</span>
            <div className="button-container">
              <button className="play-button"onClick={() => handleSearchMovie(item)}>Search on YouTube</button>
              <button className="remove-button" onClick={() => handleRemove(item.id)}>Remove</button>
              <button className="edit-button" onClick={() => handleEdit(item.id)}>Edit</button>
            </div>
          </li>
        ))}
      </ul>

    </div>

  );
};

export default StreamList;
