import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid'; // Import uuid for unique IDs
import axios from 'axios';
import './StreamList.css';

const StreamList = () => {
  const [input, setInput] = useState('');
  const [inputList, setInputList] = useState([]);
  const [editText, setEditText] = useState(''); // To store the current text when editing
  const [editId, setEditId] = useState(null); // To track which item is being edited
  const [message, setMessage] = useState(''); // To store feedback messages
  const [listName, setListName] = useState(''); // New state for list name
  const [showSaveInput, setShowSaveInput] = useState(false); // Toggle for list name input
  const [savedLists, setSavedLists] = useState(() => {
    // Retrieve 'savedLists' from localStorage
    const lists = JSON.parse(localStorage.getItem('savedLists'));
    // Set 'savedLists' to an empty array if nothing was stored or if 'lists' is not an array
    return Array.isArray(lists) ? lists : [];
});
  const [showSavedListsModal, setShowSavedListsModal] = useState(false); // Toggle modal visibility
  const [editSavedLists, setEditSavedLists] = useState(false); // Tracks edit mode for saved lists
  const [editIndex, setEditIndex] = useState(null); // Index of the list being edited
const [editedName, setEditedName] = useState(''); // Holds the edited name

  const navigate = useNavigate();
  const YOUTUBE_API_KEY = process.env.REACT_APP_YOUTUBE_API_KEY; 

   // Load inputList from localStorage when the component mounts
   useEffect(() => {
    const storedList = localStorage.getItem('inputList');
    if (storedList) {
      setInputList(JSON.parse(storedList));
    }
  }, []);

  // Save inputList to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('inputList', JSON.stringify(inputList));
  }, [inputList]);

  useEffect(() => {
    const lists = JSON.parse(localStorage.getItem('savedLists'));
    setSavedLists(Array.isArray(lists) ? lists : []); // Make sure 'savedLists' is an array
}, []);


useEffect(() => {
  // Retrieve the saved list from local storage and parse it as a JSON object
  const savedList = JSON.parse(localStorage.getItem('savedList')) || [];

  // Update the input list state with the retrieved saved list
  setInputList(savedList);
}, []); // Empty dependency array ensures this effect runs only once on component mount


const handleSaveList = () => {
  console.log('handleSaveList triggered');
  if (listName.trim()) {
      const savedLists = JSON.parse(localStorage.getItem('savedLists')) || {};

      savedLists[listName] = inputList;

      // Log to check if savedLists and inputList have the expected values
      console.log("Saving to localStorage:", savedLists);

      localStorage.setItem('savedLists', JSON.stringify(savedLists));

      setMessage('List saved successfully!');
      setTimeout(() => setMessage(''), 1000);
  } else {
      console.error("List name is empty or invalid.");
  }
};




