import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import { v4 as uuidv4 } from 'uuid'; // Import UUID function to generate unique IDs
import './StreamList.css'; 

const StreamList = () => {
  const [input, setInput] = useState(''); 
  const [inputList, setInputList] = useState([]); 
  const [editText, setEditText] = useState(''); // To store the current text when editing
  const [editId, setEditId] = useState(null); // To track which item is being edited
  const [isVideoPlaying, setIsVideoPlaying] = useState(false); // To manage video visibility
  const [videoUrl, setVideoUrl] = useState(''); // URL of the video to play
  const [isDialogOpen, setIsDialogOpen] = useState(false); 
  const navigate = useNavigate(); 

  const handleInputChange = (event) => {
    setInput(event.target.value); 
  };

  const handleSubmit = (event) => {
    event.preventDefault(); 
    if (input.trim()) { 
      setInputList([...inputList, { id: uuidv4(), text: input }]); // Add a new item with a unique UUID
      setInput(''); 
    }
  };

  const handleRemove = (id) => {
    setInputList(inputList.filter((item) => item.id !== id)); // Remove the item by its unique ID
  };

  const handleEdit = (id) => {
    const itemToEdit = inputList.find((item) => item.id === id);
    setEditText(itemToEdit.text); 
    setEditId(id); // Set the ID of the item being edited
  };

  const handleEditSubmit = (event) => {
    event.preventDefault();
    if (editText.trim() && editId) {
      setInputList(inputList.map(item =>
        item.id === editId ? { ...item, text: editText } : item
      ));
      setEditText(''); 
      setEditId(null); // Clear the edit ID after submission
    }
  };

  const handleClick = () => {
    setIsDialogOpen(true); 
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false); 
  };

  const handlePlayVideo = (id) => {
    const movieToPlay = inputList.find((item) => item.id === id); 
    setVideoUrl(`https://sample-videos.com/video123.mp4?movie=${movieToPlay.text}`); // Mock video URL
    setIsVideoPlaying(true); 
  };

  const handleGoToCart = () => {
    navigate('/cart'); 
  };

  const handleGoToMovie = () => {
    navigate('/Movies'); 
  };

  return (
    <div>

      <div className="header-container">
        <span className="material-symbols-outlined">theaters</span>
        <h1 className="inline-heading">Stream List</h1>
        <span className="material-symbols-outlined">theaters</span>
      </div>

      <form className="input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Enter Movie"
          className="input-box"
        />
        <button type="submit" className="submit-button">Add Movie</button>
        <button type="button" className="save-button" onClick={handleGoToMovie}>Save List</button>
      </form>

      <ul className="input-list">
        {inputList.map((item) => (
          <li key={item.id} className="list-item">
            <span>{item.text}</span>
            <div className="button-container">
              <button className="play-button" onClick={() => handlePlayVideo(item.id)}>Play</button>
              <button className="remove-button" onClick={() => handleRemove(item.id)}>Remove</button>
              <button className="edit-button" onClick={() => handleEdit(item.id)}>Edit</button>
            </div>
          </li>
        ))}
      </ul>

      {/* Edit form (only visible when editing an item) */}
      {editId && (
        <form className="edit-form" onSubmit={handleEditSubmit}>
          <input
            type="text"
            value={editText}
            onChange={(e) => setEditText(e.target.value)}
            placeholder="Edit Movie"
            className="edit-input-box"
          />
          <button type="submit" className="edit-submit-button">Submit Edit</button>
        </form>
      )}

      {/* Dialog for subscription */}
      {isDialogOpen && (
        <div className="dialog">
          <button className="close-button" onClick={handleCloseDialog}>X</button>
          <button className="go-button" onClick={handleGoToCart}>Subscribe</button>
          <> or </>
          <button className="go-button" onClick={handleGoToCart}>Login</button>
        </div>
      )}

      {/* Video Player */}
      {isVideoPlaying && (
        <div className="video-player">
          <video src={videoUrl} controls autoPlay />
        </div>
      )}
    </div>
  );
};

export default StreamList;
