import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios';
import './StreamList.css';

const StreamList = () => {
  const [input, setInput] = useState('');
  const [inputList, setInputList] = useState([]);
  const [editText, setEditText] = useState('');
  const [editId, setEditId] = useState(null);
  const [message, setMessage] = useState('');
  const [listName, setListName] = useState('');
  const [showSaveInput, setShowSaveInput] = useState(false);
  const [savedLists, setSavedLists] = useState([]);
  const [showSavedListsModal, setShowSavedListsModal] = useState(false);
  const [editSavedLists, setEditSavedLists] = useState(false);
  const [editIndex, setEditIndex] = useState(null);
  const [editedName, setEditedName] = useState('');

  const navigate = useNavigate();
  const YOUTUBE_API_KEY = process.env.REACT_APP_YOUTUBE_API_KEY;

  // Load inputList and savedLists from localStorage when the component mounts
  useEffect(() => {
    const storedInputList = localStorage.getItem('inputList');
    if (storedInputList) {
      setInputList(JSON.parse(storedInputList));
    }

    const lists = JSON.parse(localStorage.getItem('savedLists')) || [];
    setSavedLists(lists);
  }, []);

  // Save inputList to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('inputList', JSON.stringify(inputList));
  }, [inputList]);

  const handleSaveList = () => {
    if (listName.trim()) {
      const savedLists = JSON.parse(localStorage.getItem('savedLists')) || {};
      savedLists[listName] = inputList;
      localStorage.setItem('savedLists', JSON.stringify(savedLists));
      setMessage('List saved successfully!');
      setTimeout(() => setMessage(''), 1000);
    }
  };

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (input.trim()) {
      setInputList([...inputList, { id: uuidv4(), text: input }]);
      setInput('');
      setMessage('Movie added successfully!');
      setTimeout(() => setMessage(''), 1000);
    }
  };

  const handleRemove = (id) => {
    const updatedList = inputList.filter(item => item.id !== id);
    setInputList(updatedList);
    const savedLists = JSON.parse(localStorage.getItem('savedLists')) || {};
    savedLists[listName] = updatedList;
    localStorage.setItem('savedLists', JSON.stringify(savedLists));
    setMessage('Movie removed successfully!');
    setTimeout(() => setMessage(''), 1000);
  };

  const handleEdit = (id) => {
    const itemToEdit = inputList.find((item) => item.id === id);
    setEditText(itemToEdit.text);
    setEditId(id);
  };

  const handleEditSubmit = (event) => {
    event.preventDefault();
    if (editText.trim() && editId) {
      const updatedList = inputList.map(item => 
        item.id === editId ? { ...item, text: editText } : item
      );

      setInputList(updatedList);
      const savedLists = JSON.parse(localStorage.getItem('savedLists')) || {};
      savedLists[listName] = updatedList;
      localStorage.setItem('savedLists', JSON.stringify(savedLists));

      setEditText('');
      setEditId(null);
      setMessage('Movie edited successfully!');
      setTimeout(() => setMessage(''), 1000);
    }
  };

  const handleEditCancel = () => {
    setEditText('');
    setEditId(null);
  };

  const handleSearchMovie = async (movieTitle) => {
    try {
      const searchResponse = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=55c3657d92dd22cabb8b1efba6e60e6a&query=${encodeURIComponent(movieTitle)}`);
      const movie = searchResponse.data.results[0];

      if (movie) {
        const movieId = movie.id;
        const reviewsResponse = await axios.get(`https://api.themoviedb.org/3/movie/${movieId}/reviews?api_key=55c3657d92dd22cabb8b1efba6e60e6a`);
        const reviews = reviewsResponse.data.results;

        navigate(`/movie/${movieId}`, { state: { reviews, movieTitle } });
      } else {
        console.error("Movie not found");
      }
    } catch (error) {
      console.error("Error fetching movie reviews:", error);
    }
  };

  const handleShowSaveInput = () => {
    setShowSaveInput(true);
  };

  const handleViewSavedLists = () => {
    setShowSavedListsModal(true);
  };

  const handleSelectSavedList = (selectedList) => {
    setInputList(selectedList.items);
    setShowSavedListsModal(false);
  };

  const handleRemoveSavedList = (id) => {
    const updatedLists = savedLists.filter(list => list.id !== id);
    setSavedLists(updatedLists);
    localStorage.setItem('savedLists', JSON.stringify(updatedLists));
  };

  const handleUpdateSavedList = (index) => {
    const updatedLists = [...savedLists];
    updatedLists[index].name = editedName;
    setSavedLists(updatedLists);
    localStorage.setItem('savedLists', JSON.stringify(updatedLists));
    setEditedName('');
    setEditIndex(null);
    setEditSavedLists(false);
  };

  const handleGoToCart = () => {
    navigate('/cart');
  };

  return (
    <div className="stream-list-container">
      <div className="header-container">
        <span className="material-symbols-outlined">theaters</span>
        <h1 className="inline-heading">Stream List</h1>
        <span className="material-symbols-outlined">theaters</span>
      </div>

      {message && <p className="feedback-message">{message}</p>}

      <form className="input-form" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Enter Movie"
          className="input-box"
        />
        <button type="submit" className="submit-button">Add Movie</button>

        {showSaveInput ? (
          <div>
            <input
              type="text"
              value={listName}
              onChange={(e) => setListName(e.target.value)}
              placeholder="Enter List Name"
            />
            <button className="save-button" onClick={handleSaveList}>Save List</button>
          </div>
        ) : (
          <button className="save-button" onClick={handleShowSaveInput}>Save List</button>
        )}
        <button className="save-button" onClick={handleViewSavedLists}>View All Saved Lists</button>
      </form>

      <ul className="input-list">
        {inputList.map((item) => (
          <li key={item.id} className="input-list-item">
            <span>{item.text}</span>
            <div className="button-container">
              <button className="play-button" onClick={() => handleSearchMovie(item.text)}>
                <span className="material-symbols-outlined">slideshow</span>
              </button>
              <button className="remove-button" onClick={() => handleRemove(item.id)}>
                <span className="material-symbols-outlined">delete</span>
              </button>
              <button className="edit-button" onClick={() => handleEdit(item.id)}>
                <span className="material-symbols-outlined">edit</span>
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StreamList;
