import React, { useState, useEffect } from 'react';
import './Movie.css';
import { useParams } from 'react-router-dom';

const Movies = () => {
  const { title } = useParams();
  const [videoId, setVideoId] = useState(null);
  const YOUTUBE_API_KEY = 'AIzaSyDvS2KFydtyihEeFahH7yti5cDekE8cH4g';

  useEffect(() => {
    const fetchVideo = async () => {
      const response = await fetch(
        `https://www.googleapis.com/youtube/v3/search?part=snippet&type=video&q=${encodeURIComponent(title)}&key=${YOUTUBE_API_KEY}`
      );
      const data = await response.json();
      if (data.items.length > 0) {
        setVideoId(data.items[0].id.videoId); // Get the first video
      }
    };
    fetchVideo();
  }, [title]);

  return (
    <div>
      <h2>Movie: {title}</h2>
      {videoId ? (
        <iframe
          width="560"
          height="315"
          src={`https://www.youtube.com/embed/${videoId}`}
          title="YouTube video player"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Movies;
